15→pub fn ensure_store(repo: &Path) -> anyhow::Result<StoreMetadata> {
16→    let db_path = index_db_path(repo);
17→    let parent = db_path.parent().unwrap_or_else(|| Path::new("."));
18→    let create_dir_result = fs::create_dir_all(parent).with_context(|| {
19→        format!(
20→            "failed to create index directory for {}",
21→            parent.to_string_lossy()
22→        )
23→    });
24→    create_dir_result?;
25→
26→    let connection = Connection::open(&db_path)
27→        .with_context(|| format!("failed to open sqlite database {}", db_path.display()))
28→        .map_err(|error| with_corruption_hint(error, &db_path))?;
29→    schema::bootstrap_schema(&connection).map_err(|error| with_corruption_hint(error, &db_path))?;
30→    let schema_version = schema::read_schema_version(&connection)
31→        .map_err(|error| with_corruption_hint(error, &db_path))?;
32→
33→    Ok(StoreMetadata {
34→        db_path,
35→        schema_version,
36→    })
37→}
