43→fn with_corruption_hint(error: anyhow::Error, db_path: &Path) -> anyhow::Error {
44→    if is_corruption_error(&error) {
45→        return anyhow!(
46→            "index database appears corrupted at {}. delete this file and rerun `repo-scout index --repo <path>`.",
47→            db_path.display()
48→        );
49→    }
50→    error
51→}
