53→fn is_corruption_error(error: &anyhow::Error) -> bool {
54→    error.chain().any(|cause| {
55→        let Some(sqlite_error) = cause.downcast_ref::<rusqlite::Error>() else {
56→            return false;
57→        };
58→
59→        matches!(
60→            sqlite_error.sqlite_error_code(),
61→            Some(ErrorCode::DatabaseCorrupt | ErrorCode::NotADatabase)
62→        )
63→    })
64→}
