 7→pub fn repo_scout_cmd() -> Command {
 8→    let env_candidates = [
 9→        "CARGO_BIN_EXE_repo_scout",
10→        "CARGO_BIN_EXE_repo-scout",
11→        "CARGO_BIN_EXE_codex_5_3",
12→        "CARGO_BIN_EXE_codex-5-3",
13→    ];
14→
15→    for name in env_candidates {
16→        if let Some(path) = std::env::var_os(name).map(PathBuf::from)
17→            && path.is_file()
18→        {
19→            return Command::new(path);
20→        }
21→    }
22→
23→    let mut target_dir = std::env::current_exe().expect("test executable path should be available");
24→    target_dir.pop();
25→    if target_dir.ends_with("deps") {
26→        target_dir.pop();
27→    }
28→
29→    let bin_candidates = ["repo-scout", "codex-5-3"];
30→    for bin_name in bin_candidates {
31→        let path = target_dir.join(format!("{bin_name}{}", std::env::consts::EXE_SUFFIX));
32→        if path.is_file() {
33→            return Command::new(path);
34→        }
35→    }
36→
37→    panic!(
38→        "repo-scout binary not found via CARGO_BIN_EXE_* or {}",
39→        target_dir.display()
40→    );
41→}
